from Crypto.Util.number import getPrime
flag = "cyber{fakeflag!}"
encoded = int.from_bytes(flag.encode(), "big")
e = 65537
p = getPrime(1024) # Generates prime of size 1024 bits
print("p = ", p)
q = getPrime(1024)
n = p * q
print("n = ",n)

c = pow(encoded, e, n)
print("c = ", c)

